import {BUY_CAKE} from './cakeTypes'

const initialState = {
    numOfCakes : 10
}


const cakeReducer = (state = initialState, action) => {
    // console.log(state);
    switch (action.type) {
        case BUY_CAKE:     
            return{
                ...state, numOfCakes : state.numOfCakes - 1
            }
        default: 
            return{
                state 
            }
    }

}

console.log(cakeRedu);


export default cakeReducer
