import {BUY_CAKE} from './cakeTypes'

const initialState = {
    numOfCakes : 10
}


const cakeReducer = (state = initialState, action) => {

    switch (action.type) {
        case BUY_CAKE:  
            return{
                ...state, numOfCakes : state.state.numOfCakes - 1
            }
        default: 
            return{
                state
            }
    }
    console.log(state)
}

export default cakeReducer
