import React, { Component } from 'react'
import './styles.css'
import { storeproducts } from "../../components/homecontent/Data";
import { connect } from "react-redux";
import { decrementAction } from "../../redux/cart/decrementCart";
import { incrementAction } from "../../redux/cart/incrementCart";

class CartItems extends Component {
    constructor(props) {
        super(props)

        this.state = {
            products: storeproducts
        }
    }


    render() {
        var cartItem = this.props.cart.map(cartz => (cartz))
        var lists = [];
        for (let i = 0; i < cartItem.length; i++) {
            lists[i] = this.state.products.find(pro => (pro.id == cartItem[i].product_id))
            lists[i].quantity = cartItem[i].quantity
        }
        return (
            <div>
                <div className="container">
                    <table className="table table-condensed table-light table-bordered">
                        <thead><tr>
                            <th>Sl No</th>
                            <th>Name</th>
                            <th></th>
                            <th>Price</th>
                            <th>Quantity</th>
                            <th>Total</th>
                            <th>Action</th>
                        </tr></thead>
                        <tbody>
                            {
                                lists.map(cartProd => (
                                    <tr key={cartProd.id}>
                                        <td>1</td>
                                        <td>{cartProd.name}</td>
                                        <td><img className="image-sizes" src={cartProd.imgUrl} alt="No Image" /></td>
                                        <td>{cartProd.price}</td>
                                        <td><button onClick = {this.props.decrementAction}><i className = "fas fa-minus"></i></button><input type="number" value={cartProd.quantity} className="quantity_input" readOnly/><button onClick={this.props.incrementAction}><i className = "fas fa-plus"></i></button></td>
                                        <td>{cartProd.quantity * cartProd.price}</td>
                                        <td><button className="btn btn-danger"><i className="fas fa-trash"></i></button></td></tr>
                                ))}
                        </tbody>

                    </table>
                </div>

            </div>
        )
    }
}

const mapStateToProps = (states) => {
    // console.log(states);
    return {
        cart: states.cart
    }
}

const mapDispatchToProps = (dispatch) => {
    return{
        decrementAction : () => dispatch(decrementAction()),
        incrementAction : () => dispatch(incrementAction())
    }
}

export default connect(mapStateToProps, mapDispatchToProps)(CartItems)
