import React, { Component } from 'react'
import './styles.css'
import {Link, Redirect} from "react-router-dom";
import {storeproducts} from './Data'
class Products extends Component {
    constructor(props) {
        super(props)

        this.state = {
             products : storeproducts,
             selProd : 1
        }
    }

    detailsHandler = (id)=>{
        this.setState({
            selProd : id
        }, () => {
            alert(this.state.selProd)
        });
    }

    render() {
        // console.log(this.state.products)
        if (this.state.redirect) {
            return <Redirect push to={{ pathname : "/details", prdId : {id : this.state.selProd}}} />;
          }

        return (
            <div>
                <div className="row">
                {this.state.products.map((product) => (

                    <div key = {product.id} className="col-3 text-center">
                        <Link className="to-details" onClick={() => this.detailsHandler(product.id)} to={{ pathname : "/details", prdId : {id : this.state.selProd}}}>
                        <div className="image-sizes">
                            <img alt="No Search" src={product.imgUrl}/>
                            </div>
                            <div className="details-product">
                            <p><b>{product.name}</b></p>
                            <p className="price"><i className="fa fa-rupee-sign"></i> {product.price}</p>
                            </div>
                            </Link>  
                            <button className="btn btn-add-to-cart">Add To Cart</button>
                        </div>


                ))}

            </div>
            </div>
        )
    }
}

export default Products
