import { INCREMENT_COUNT, DECREMENT_COUNT, ADD_TO_CART } from "./actionType";

const initialState = {
    cart: [{
        product_id: 1,
        quantity: 2
    }, {
        product_id: 2,
        quantity: 1
    }]
}

const cartReducer = (state = initialState, action) => {
    switch (action.type) {
        // ****
        // While incrementing the quantity from cart

        case INCREMENT_COUNT:
            var indexs = state.cart.findIndex(cartz => (cartz.product_id == action.id))
            var back = Object.assign([], state.cart);
            back[indexs].quantity++
            return {
                ...state,
                cart: back
            }

        // Incrementing Ends
        case DECREMENT_COUNT:
            var indexs = state.cart.findIndex(cartz => (cartz.product_id == action.id))
            var back = Object.assign([], state.cart);
            back[indexs].quantity--
            return {
                ...state,
                cart: back
            }
        case ADD_TO_CART:
            return {
                ...state, cart: state.cart.concat({
                    product_id: action.id,
                    quantity: action.payload
                })
            }
        default:
            return state;
    }
}

export default cartReducer
