import {BUY_CAKE} from './cakeTypes'

const initialState = {
    numOfCakes : 10
}

const state = initialState

const cakeReducer = (state, action) => {
    console.log(state);
    switch (action.type) {
        case BUY_CAKE:     
            return{
                numOfCakes : state.numOfCakes - 1
            }
        default: 
            return{
                state
            }
    }

}

export default cakeReducer
