import React, { Component } from 'react'

import { createStore } from 'redux'

// Action
const BUY_CAKE = 'Buy Cake'

function buy_cake(){
    return{
        type : BUY_CAKE,
        info : "Cake Reduce"
    }
}
// Action ends

// Reducer

const initState = {
    numCake : 10
}

const reducer = (state = initState, action) => {
    switch (action.type) {
        case BUY_CAKE:
            return {...state, numCake : state.numCake - 1}

        default:
            return state
    }
}

// Reducer Ends

// Store

const store = createStore(reducer)
