import React, { Component } from 'react'
import './styles.css'
import { storeproducts } from "../../components/homecontent/Data";
import { connect } from "react-redux";

class CartItems extends Component {
    constructor(props) {
        super(props)

        this.state = {
            products: storeproducts
        }
    }


    render() {
        var cartItem = this.props.cart.map(cartz => (cartz))
        var lists = [];
        for (let i = 0; i < cartItem.length; i++) {
            lists[i] = this.state.products.find(pro => (pro.id == cartItem[i].product_id))
            lists[i].quantity = cartItem[i].quantity
        }
        return (
            <div>
                <div className="container">
                    <table className="table table-condensed table-light table-bordered">
                        <thead><tr>
                            <th>Sl No</th>
                            <th>Name</th>
                            <th></th>
                            <th>Price</th>
                            <th>Quantity</th>
                            <th>Total</th>
                            <th>Action</th>
                        </tr></thead>

                        {/* <tbody>
                            {this.state.products.map(product => (
                                <tr>
                                    <td>{product.id}</td>
                                    <td>{product.name}</td>
                                    <td>{product.name}</td>
                                    <td>{product.price}</td>
                                    <td>{product.name}</td>
                                    <td>{product.name}</td>
                                    <td>{product.name}</td>
                                </tr>
                            ))}

                        </tbody> */}
                        <tbody>
                            {
                                lists.map(cartProd => (
                                        <tr>
                                        <td>1</td>
                                        <td>{cartProd.name}</td>
                                        <td><img className = "image-sizes" src = {cartProd.imgUrl} alt = "No Image" /></td>
                                        <td>{cartProd.price}</td>
                                        <td>{cartProd.quantity}</td>
                                        <td>{cartProd.quantity *cartProd.price}</td>
                                        <td><button><i className="fa fa-remove  "></i></button></td></tr>
                                        ))}
                        </tbody>

                    </table>
                </div>

            </div>
        )
    }
}

const mapStateToProps = (states) => {
    // console.log(states);
    return {
        cart: states.cart
    }
}

export default connect(mapStateToProps)(CartItems)
