import React, { Component } from 'react'

import { createStore, combineReducers } from 'redux'

// Action
const BUY_CAKE = 'Buy Cake'
const BUY_ICECREAM = 'Buy Icecream'

function buy_cake(){
    return{
        type : BUY_CAKE,
        info : "Cake Reduce"
    }
}

function buy_icecream(){
    return{
        type : BUY_ICECREAM,
        info : "Icecream Reduce"
    }
}
// Action ends

// Reducer

const initState = {
    numCake : 10
}

const initIceState = {
    numIce : 20
}

const cakeReducer = (state = initState, action) => {
    switch (action.type) {
        case BUY_CAKE:
            return {...state, numCake : state.numCake - 1}

        default:
            return state
    }
}

const iceReducer = (state = initIceState, action) => {
    switch (action.type) {
        case BUY_ICECREAM:
            return {...state, numIce : state.numIce - 1}

        default:
            return state
    }
}

// Reducer Ends

// Store
const rootReducer = combineReducers({
    cake : cakeReducer,
    ice : iceReducer
})

const store = createStore(rootReducer)
console.log("Initial state" , store.getState());
const unsubscribe = store.subscribe(() => console.log("Updated state" , store.getState()))
store.dispatch(buy_cake());
