import { INCREMENT_COUNT, DECREMENT_COUNT, ADD_TO_CART } from "./actionType";

const initialState = {
    cart: [{
        product_id: 1,
        quantity: 2
    }, {
        product_id: 2,
        quantity: 1
    }]
}

const cartReducer = (state = initialState, action) => {

    switch (action.type) {
        case INCREMENT_COUNT:
            return state;
        case DECREMENT_COUNT:
            return state;
        case ADD_TO_CART:
            return {
                ...state, ...state.cart.concat({
                    prod
                })
            }
        default:
            return state;
    }
}

export default cartReducer
