import React, { Component } from 'react'
import '../App.css';
import { BrowserRouter as Router, Switch, Route, Link } from "react-router-dom";
import HomeComponent from './HomeComponent';
class LoginComponent extends Component {

    constructor(props) {
        super(props)
    
        this.state = {
             username : '',
             password : ''
        }
    }

    onChangeHandler = e =>{
        this.setState({
            [e.target.name] : e.target.value
        })
    }

    submitHandler = e => {
        
    }
    

    
    render() {
        const {username, password} = this.state
        return (
            <Router>
<div className = "bgcolor">
                <div className="card card-width">
                    <div className = "card-header text-center">
                        <h5>Account Login</h5>
                    </div>
                    <div className="card-body">
                        <form onSubmit={this.submitHandler}>
                            {/* For User Name */}
                            <div className="form-group">
                                <label htmlFor="userName">Username</label>
                                <input type = "text" name="username" value={username} onChange = {this.onChangeHandler} id="userName" className="form-control" placeholder = "User Name" />
                            </div>
                            {/* For Password */}
                            <div className="form-group">
                                <label htmlFor="password">Password</label>
                                <input type = "password" name="password" value = {password} onChange = {this.onChangeHandler}  id="password" className="form-control" placeholder = "Password" />
                            </div>

                            <button className="btn btn-darkblue" type="submit">Login</button>
                            {/* Submit Button */}
                        </form>
                    </div>
                </div>
            </div>
            <Switch>
                <Route exact path='/home' component={HomeComponent} />
            </Switch>
            </Router>

            
        )
    }
}

export default LoginComponent
