const BUY_CAKE = 'BUY_CAKE' 

const initialState = {
    numOfCakes : 10
}


const cakeReducer = (state = initialState, action) => {
    // console.log(state)
    switch (action.type) {
        case BUY_CAKE:  
            return({
                ...state, numOfCakes : state.state.numOfCakes - 1
            })
        default: 
            return{
                state
            }
    }

}

export default cakeReducer
